import React, { useState, useEffect } from "react";
import Web3 from "web3";
import { ethers } from "ethers";
import Contract from './Contract';
require('dotenv').config();

//get .env params
const RpcHttpUrl = process.env.REACT_APP_RPC_HTTP_URL;
const ReceiverAddress = process.env.REACT_APP_RECEIVER_ADDRESS;
const SolanaAPI = process.env.REACT_APP_SOLANA_API;
const AuthAPI = process.env.REACT_APP_AUTH_API;

//create web3 connection
const web3Eth = new Web3(new Web3.providers.HttpProvider(RpcHttpUrl));

function Main(props) {
    //set params
    const [transferAmount, setTransferAmount] = useState("");
    const [receiverAddress, setReceiverAddress] = useState("");
    const [showResults, setShowResults] = useState(false);
    const [hideButton, setHideButton] = useState(false);
    const [isWalletConnected, setIsWalletConnected] = useState(false);
    var signer
    useEffect(() => {
    }, [])
    try {        
        if (typeof window !== 'undefined') {
            console.log("token: ", localStorage.getItem("token"));
            console.log("wallet: ", localStorage.getItem("wallet"));            
            //this.state.receiverAddress = localStorage.getItem("wallet");
            //setReceiverAddress(localStorage.getItem("wallet").toString());
            if (window.ethereum) {
                window.ethereum.enable();
                const provider = new ethers.providers.Web3Provider(window.ethereum);
                signer = provider.getSigner();
                if (signer.provider.provider.selectedAddress) {
                    setIsWalletConnected(true);
                    provider.getBalance(signer.provider.provider.selectedAddress).then((balance) => {                       
                        const balanceInEth = ethers.utils.formatEther(balance);
                        console.log(`balance: ${balanceInEth} ETH`)
                    })
                }
            }
            else {
                console.log("Error")
            }
        }
    } catch (error) {
        console.log("getAccount ~ error", error)
}

const connectWalletHandler = () => {
    if (typeof window !== undefined) {
        if (window.ethereum && window.ethereum.isMetaMask) {
            console.log("MetaMask Here!");
            window.ethereum
                .request({ method: "eth_requestAccounts" })
                .then((result) => {
                    //accountChangedHandler(result[0]);
                    console.log("ðŸš€ ~ file: Navbar.js ~ line 24 ~ .then ~ result[0]", result[0])
                    //setConnButtonText("Connected");
                    //getAccountBalance(result[0]);
                })
                .catch((error) => {
                    //setErrorMessage(error.message);
                });
        } else {
            console.log("Need to install MetaMask");
            //setErrorMessage("Please install MetaMask browser extension to interact");
        }
    }
};

async function transfer(){
    //console.log("ReceiverAddress: ", ReceiverAddress);
    console.log("Transferring the tokens...");
    const value = web3Eth.utils.toWei(transferAmount.toString(), 'Ether');
    const hash = await Contract.transfer(ReceiverAddress,value);
    setHideButton(true);
    console.log("hash: ", hash);  
    console.log("receiverAddress: ", receiverAddress);  
    console.log('Authorization: ', 'Bearer '+localStorage.getItem("token").replaceAll('"', ''));
    fetch(AuthAPI+"/setBridgeInfo", {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Authorization':'Bearer '+localStorage.getItem("token").replaceAll('"', '')
        },
        body: JSON.stringify({
            solanaAddress:receiverAddress != '' ? receiverAddress : localStorage.getItem("wallet"),
            evmAddress:signer.provider.provider.selectedAddress,
            amount:transferAmount,
        })
    });
    await fetch(SolanaAPI, {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Authorization':'Bearer '+localStorage.getItem("token").replaceAll('"', '')
        },
        body: JSON.stringify({
            address:receiverAddress != '' ? receiverAddress : localStorage.getItem("wallet"),
            evmAddress:signer.provider.provider.selectedAddress,
            amount:transferAmount,
        })
    });
    setShowResults(true);
    console.log("Transferred successfully!");
}
    return (
        <div>
            <br />
            <div style={{ fontSize: "2rem" }}>
                Bridge o5o BEP20 Token to o5o SPL Token                            
            </div>
            <div>
            {!hideButton && (
                    <div>
                        <button 
                            type="submit"
                            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 border border-blue-700 rounded"
                            onClick={() => transfer()}
                        >Send</button>
                    </div>)
                }
            {/* {
                !isWalletConnected && ( 
                    <div>
                    <button onClick={connectWalletHandler}><img
                    class='mt-1'
                    src='/metamask_icon.svg'
                    style={{"width":"40px", height:"30px"}} />
                    </button></div>
                )
            } */}
            </div>    
            <br/>
            <div style={{fontSize:"1.5rem", "paddingBottom":"10px"}}>
                Solana Wallet Address :
            </div>
            <div>
                <input
                    type="text"                    
                    style={{height:"1.5vw"}}
                    value = {localStorage.getItem("wallet")}
                    onChange={(event) =>
                        setReceiverAddress(event.target.value)
                    }
                    placeholder="0x0000....."
                />
            </div>
            <br />
            <div style={{ fontSize: "1.5rem", "paddingBottom":"10px" }}>
                o5o Token Amount :
            </div>
            <div>
                <input
                    type="text"
                    style={{ height: "1.5vw"}}
                    onChange={(event) =>
                        setTransferAmount(event.target.value)
                    }
                    placeholder="000...."
                /> tokens
            </div>
            <br />
            <div>
            {!hideButton && (
                    <div>
                        <button 
                            type="submit"
                            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 border border-blue-700 rounded"
                            onClick={() => transfer()}
                        >Send</button>
                    </div>)
                }
                {showResults && (
                    
                    <div style={{color: "green", "fontSize": "1.5rem"}}>
                        Tokens transferred successfully!!!
                    </div>)
                }
                {/* {!showResults && hideButton && (
                    
                    <div style={{color: "red", "fontSize": "1.5rem"}}>
                        There is an error occurred while transferring the token
                    </div>)
                } */}
         </div>
        </div>
    );
}

export default Main;
