/* eslint-disable jsx-a11y/alt-text */
import { Link } from "react-router-dom";
import logo from "../assets/logo.png";
import { useMediaQuery } from "react-responsive";
import { WalletMultiButton, } from "@solana/wallet-adapter-react-ui";
import WalletBtnImg from '../assets/wallet-icon.png';
import { useMemo, useState } from 'react';
import { useWallet } from '@solana/wallet-adapter-react';

import { RiUser3Fill } from "react-icons/ri";
import { REWARD_TOKEN_DECIMALS } from "../../constant/env";

interface NavbarInterface {
  globalData: any;
}

const Navbar = (props: NavbarInterface | undefined = undefined) => {
  //Media Quearry
  const isTabletOrMobile = useMediaQuery({ query: "(max-width: 950px)" });
  const { publicKey, wallet, disconnect } = useWallet();
  const base58 = useMemo(() => publicKey === null || publicKey === void 0 ? void 0 : publicKey.toBase58(), [publicKey]);
  const content = useMemo(() => {
    if (!wallet || !base58)
      return null;
    return base58.slice(0, 4) + '..' + base58.slice(-4);
  }, [wallet, base58]);

  return (
    <div className="border-b border-grey-light h-24">
      <div className="flex items-center border-b h-24">
        {props &&  props.globalData ? <span className="w-full ml-10">Total Locked Value : {props.globalData ? Math.floor(props.globalData.availableTokenAmount.toNumber() / REWARD_TOKEN_DECIMALS * 100) / 100 : 0}</span>:<span className="w-full"></span>}
        <WalletMultiButton className="mr-12" />
        <RiUser3Fill className="mr-24" />
      </div>
    </div>
  );
};

export default Navbar;
