module.exports = typeof self == 'object' ? self.FormData : window.FormData;
import { Storage, UploadResult } from '../Storage';
import { Buffer } from 'buffer';
import axios from 'axios';
import FormData from 'form-data';

const ARWEAVE_URL = 'https://arweave.net';
const LAMPORT_MULTIPLIER = 10 ** 9;
const WINSTON_MULTIPLIER = 10 ** 12;

export interface ArweaveUploadResult extends UploadResult {
  messages?: {
    filename: string;
    status: 'success' | 'fail';
    transactionId?: string;
    error?: string;
  }[];
}

export interface ArweaveStorageCtorFields {
  endpoint: string;
  env: 'mainnet-beta' | 'testnet' | 'devnet';
}

export class ArweaveStorage implements Storage {
  readonly endpoint: string;
  readonly env: string;

  constructor({ endpoint, env }: ArweaveStorageCtorFields) {
    this.endpoint = endpoint;
    this.env = env;
  }

  async getAssetCostToStore(files: Map<string, Buffer>, arweaveRate: number, solanaRate: number) {
    const buffers = Array.from(files.values());
    const totalBytes = buffers.reduce((sum, f) => (sum += f.byteLength), 0);
    const txnFeeInWinstons = parseInt(await (await axios(`${ARWEAVE_URL}/price/0`)).data);
    const byteCostInWinstons = parseInt(
      await (
        await axios(`${ARWEAVE_URL}/price/${totalBytes.toString()}`)
      ).data,
    );
    const totalArCost =
      (txnFeeInWinstons * buffers.length + byteCostInWinstons) / WINSTON_MULTIPLIER;
    // To figure out how many lamports are required, multiply ar byte cost by this number
    const arMultiplier = arweaveRate / solanaRate;
    // We also always make a manifest file, which, though tiny, needs payment.
    return LAMPORT_MULTIPLIER * totalArCost * arMultiplier * 1.1;
  }

  async upload(
    files: Map<string, Buffer>,
    mintKey: string,
    txid: string,
  ): Promise<ArweaveUploadResult> {
    const fileEntries = Array.from(files.entries());
    const tags = fileEntries.reduce(
      (acc: Record<string, Array<{ name: string; value: string }>>, [fileName]) => {
        acc[fileName] = [{ name: 'mint', value: mintKey }];
        return acc;
      },
      {},
    );

    const body = new FormData();

    body.append('tags', JSON.stringify(tags));
    body.append('transaction', txid);
    body.append('env', this.env);
    fileEntries.map(([, file]) => {
      body.append('file[]', file);
    });

    // TODO: I hate to do this, but it seems to be like an upstream problem:
    // https://github.com/jimmywarting/FormData/issues/133
    // I'll make sure to track it. - Danny
    const response = await axios.post(this.endpoint, body);

    if (response.data.error) {
      return Promise.reject(new Error(response.data.error));
    }

    return response.data;
  }
}
import { clusterApiUrl, Commitment, Connection as SolanaConnection } from '@solana/web3.js';

export enum ChainId {
  MainnetBeta = 101,
  Testnet = 102,
  Devnet = 103,
}

export const ENV: Record<string, { endpoint: string; ChainId: ChainId }> = {
  devnet: {
    endpoint: clusterApiUrl('devnet'),
    ChainId: ChainId.Devnet,
  },
  'mainnet-beta': {
    endpoint: 'https://api.metaplex.solana.com/',
    ChainId: ChainId.MainnetBeta,
  },
  'mainnet-beta (Solana)': {
    endpoint: 'https://api.mainnet-beta.solana.com',
    ChainId: ChainId.MainnetBeta,
  },
  'mainnet-beta (Serum)': {
    endpoint: 'https://solana-api.projectserum.com/',
    ChainId: ChainId.MainnetBeta,
  },
  testnet: {
    endpoint: clusterApiUrl('testnet'),
    ChainId: ChainId.Testnet,
  },
};

export class Connection extends SolanaConnection {
  constructor(endpoint: keyof typeof ENV | string = 'mainnet-beta', commitment?: Commitment) {
    if (endpoint in ENV) endpoint = ENV[endpoint].endpoint;
    super(endpoint, commitment);
  }
}
import { Keypair, PublicKey, Transaction } from '@solana/web3.js';

export interface Wallet {
  publicKey: PublicKey;
  signTransaction(tx: Transaction): Promise<Transaction>;
  signAllTransactions(txs: Transaction[]): Promise<Transaction[]>;
}

export class NodeWallet implements Wallet {
  constructor(readonly payer: Keypair) {}

  async signTransaction(tx: Transaction): Promise<Transaction> {
    tx.partialSign(this.payer);
    return tx;
  }

  async signAllTransactions(txs: Transaction[]): Promise<Transaction[]> {
    return txs.map((tx) => {
      tx.partialSign(this.payer);
      return tx;
    });
  }

  get publicKey(): PublicKey {
    return this.payer.publicKey;
  }
}
import { AccountInfo, Commitment, PublicKey, Connection } from '@solana/web3.js';
import { AnyPublicKey } from '@metaplex/types';
import { Buffer } from 'buffer';

export type AccountConstructor<T> = {
  new (pubkey: AnyPublicKey, info: AccountInfo<Buffer>): T;
};

export class Account<T = unknown> {
  readonly pubkey: PublicKey;
  readonly info: AccountInfo<Buffer>;
  data: T;

  constructor(pubkey: AnyPublicKey, info?: AccountInfo<Buffer>) {
    this.pubkey = new PublicKey(pubkey);
    this.info = info;
  }

  static from<T>(this: AccountConstructor<T>, account: Account<unknown>) {
    return new this(account.pubkey, account.info);
  }

  static async load<T>(
    this: AccountConstructor<T>,
    connection: Connection,
    pubkey: AnyPublicKey,
  ): Promise<T> {
    const info = await Account.getInfo(connection, pubkey);

    return new this(pubkey, info);
  }

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  static isCompatible(data: Buffer): boolean {
    throw new Error(`method 'isCompatible' is not implemented`);
  }

  static async getInfo(connection: Connection, pubkey: AnyPublicKey) {
    const info = await connection.getAccountInfo(new PublicKey(pubkey));
    if (!info) {
      throw new Error(`Unable to find account: ${pubkey}`);
    }

    return { ...info, data: Buffer.from(info?.data) };
  }

  static async getInfos(
    connection: Connection,
    pubkeys: AnyPublicKey[],
    commitment: Commitment = 'recent',
  ) {
    const BATCH_SIZE = 99; // Must batch above this limit.

    const promises: Promise<Map<AnyPublicKey, AccountInfo<Buffer>> | undefined>[] = [];
    for (let i = 0; i < pubkeys.length; i += BATCH_SIZE) {
      promises.push(
        Account.getMultipleAccounts(
          connection,
          pubkeys.slice(i, Math.min(pubkeys.length, i + BATCH_SIZE)),
          commitment,
        ),
      );
    }

    const results = new Map<AnyPublicKey, AccountInfo<Buffer>>();
    (await Promise.all(promises)).forEach((result) =>
      [...(result?.entries() ?? [])].forEach(([k, v]) => results.set(k, v)),
    );
    return results;
  }

  private static async getMultipleAccounts(
    connection: Connection,
    pubkeys: AnyPublicKey[],
    commitment: Commitment,
  ) {
    const args = connection._buildArgs([pubkeys.map((k) => k.toString())], commitment, 'base64');
    const unsafeRes = await (connection as any)._rpcRequest('getMultipleAccounts', args);
    if (unsafeRes.error) {
      throw new Error('failed to get info about accounts ' + unsafeRes.error.message);
    }
    if (!unsafeRes.result.value) return;
    const infos = (unsafeRes.result.value as AccountInfo<string[]>[]).map((info) => ({
      ...info,
      data: Buffer.from(info.data[0], 'base64'),
    })) as AccountInfo<Buffer>[];
    return infos.reduce((acc, info, index) => {
      acc.set(pubkeys[index], info);
      return acc;
    }, new Map<AnyPublicKey, AccountInfo<Buffer>>());
  }

  assertOwner(pubkey: AnyPublicKey) {
    return this.info?.owner.equals(new PublicKey(pubkey));
  }

  toJSON() {
    return {
      pubkey: this.pubkey.toString(),
      info: {
        executable: !!this.info?.executable,
        owner: this.info?.owner ? new PublicKey(this.info?.owner) : null,
        lamports: this.info?.lamports,
        data: this.info?.data.toJSON(),
      },
      data: this.data,
    };
  }

  toString() {
    return JSON.stringify(this.toJSON());
  }
}
import { PublicKey } from '@solana/web3.js';
import { deserializeUnchecked, serialize, deserialize, BinaryReader, BinaryWriter } from 'borsh';
import base58 from 'bs58';

export const extendBorsh = () => {
  (BinaryReader.prototype as any).readPubkey = function () {
    const reader = this as unknown as BinaryReader;
    const array = reader.readFixedArray(32);
    return new PublicKey(array);
  };
  (BinaryWriter.prototype as any).writePubkey = function (value: PublicKey) {
    const writer = this as unknown as BinaryWriter;
    writer.writeFixedArray(value.toBuffer());
  };
  (BinaryReader.prototype as any).readPubkeyAsString = function () {
    const reader = this as unknown as BinaryReader;
    const array = reader.readFixedArray(32);
    return base58.encode(array); // pubkey string
  };
  (BinaryWriter.prototype as any).writePubkeyAsString = function (
    value: string, // pubkey string
  ) {
    const writer = this as unknown as BinaryWriter;
    writer.writeFixedArray(base58.decode(value));
  };
};

